-- This migration was created by the Supabase CLI.
-- It creates a 'profiles' table in the public schema to store user roles and other metadata.

BEGIN;

-- Create the profiles table
CREATE TABLE public.profiles (
    id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL PRIMARY KEY,
    email TEXT UNIQUE NOT NULL,
    role TEXT DEFAULT 'student' NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Set up Row Level Security (RLS) for the profiles table
ALTER TABLE public.profiles ENABLE ROW LEVEL SECURITY;

-- Policy to allow all authenticated users to view profiles (for example, to see other users' names/roles if needed)
CREATE POLICY "Public profiles are viewable by all users." ON public.profiles FOR SELECT USING (true);

-- Policy to allow a user to manage their own profile
CREATE POLICY "Users can update their own profile." ON public.profiles
  FOR UPDATE USING (auth.uid() = id);

-- Policy to allow inserts for authenticated users (handled by trigger later)
-- We will use a trigger to insert, so direct INSERT policy might not be needed for users.
-- For admin, they can insert directly if needed.

-- Create a trigger function to insert new users into profiles table
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO public.profiles (id, email, role)
  VALUES (NEW.id, NEW.email, 'student');
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create the trigger on auth.users for new sign-ups
CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();

-- Optionally, insert/update an admin user.
-- REMEMBER TO REPLACE 'your_admin_email@example.com' WITH THE ACTUAL EMAIL!
-- You will need to manually run this part or ensure the email exists before running.
-- For a brand new user, the trigger will set their role to 'student'.
-- If you need to set an existing user as admin:
UPDATE public.profiles
SET role = 'admin'
WHERE email = 'erstr9417@himolde.no';

COMMIT;

-- BEGIN;
--
-- -- Revert: Drop the trigger
-- DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
--
-- -- Revert: Drop the trigger function
-- DROP FUNCTION IF EXISTS public.handle_new_user();
--
-- -- Revert: Drop RLS policies (implicitly dropped when table is dropped, but good practice to explicitly drop policies first)
-- DROP POLICY IF EXISTS "Public profiles are viewable by all users." ON public.profiles;
-- DROP POLICY IF EXISTS "Users can update their own profile." ON public.profiles;
--
-- -- Revert: Drop the profiles table
-- DROP TABLE IF EXISTS public.profiles;
--
-- COMMIT;

'use client';

import React, { useState, FormEvent } from 'react';
import { useSearchParams } from 'next/navigation'; // Removed useRouter

export default function AdminLoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);
  // const router = useRouter(); // Removed unused router
  const searchParams = useSearchParams();
  const queryError = searchParams.get('error');

  // Display error from query params if present
  React.useEffect(() => {
    if (queryError) {
      setError(queryError);
    }
  }, [queryError]);


  const handleSubmit = async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setError(null); // Clear previous errors

    const formData = new FormData();
    formData.append('email', email);
    formData.append('password', password);

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        body: formData,
        redirect: 'follow', // Allow Next.js to handle the redirect from the API route
      });

      // The API route handles redirects. If we get here, something went wrong with the redirect
      // or the API returned a non-redirect response.
      if (!response.redirected) {
        // If the API didn't redirect, it likely returned an error response.
        // We'll parse the error message if available or use a generic one.
        const errorText = await response.text(); // Get raw error message from API
        setError(errorText || 'An unexpected error occurred during login.');
      }
      // If response.redirected is true, the router will handle the navigation.
      // We don't need to do anything further here.

    } catch (err) {
      setError('Network error or unable to connect to the server.');
      console.error('Login form submission error:', err);
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100">
      <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-bold text-center text-gray-900">Admin Login</h2>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
            <strong className="font-bold">Error! </strong>
            <span className="block sm:inline">{error}</span>
          </div>
        )}

        <form className="space-y-4" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Email
            </label>
            <input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              placeholder="admin@example.com"
            />
          </div>
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              autoComplete="current-password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
              placeholder="********"
            />
          </div>
          <div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Sign in
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

import { createServerClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { NextResponse } from 'next/server';

import type { NextRequest } from 'next/server';
import type { Database } from '@/types/supabase'; // Assuming you have a Supabase types file

interface ResponseCookieOptions {
  domain?: string;
  expires?: Date;
  httpOnly?: boolean;
  maxAge?: number;
  path?: string;
  sameSite?: boolean | 'lax' | 'strict' | 'none';
  secure?: boolean;
  value?: string;
}

export async function POST(request: NextRequest) {
  const requestUrl = new URL(request.url);
  const formData = await request.formData();
  const email = String(formData.get('email'));
  const password = String(formData.get('password'));
  const cookieStore = await cookies();

  const supabase = createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return cookieStore.get(name)?.value;
        },
        set(name: string, value: string, options: ResponseCookieOptions) {
          cookieStore.set(name, value, options);
        },
        remove(name: string, options: { path: string }) {
          cookieStore.delete(name); // Removed options, simplified to delete by name
        },
      },
    }
  );

  // 1. Sign in the user
  const { error } = await supabase.auth.signInWithPassword({
    email,
    password,
  });

  if (error) {
    return NextResponse.redirect(
      `${requestUrl.origin}/admin/login?error=Could not authenticate user`,
      { status: 301 }
    );
  }

  // 2. Get user session to retrieve user ID
  const { data: { session } } = await supabase.auth.getSession();

  if (!session) {
    return NextResponse.redirect(
      `${requestUrl.origin}/admin/login?error=User session not found`,
      { status: 301 }
    );
  }

  // 3. Query the public.profiles table to check for 'admin' role
  type ProfileQueryResult = { role: string | null } | null;
  const { data: profile, error: profileError } = await supabase
    .from('profiles')
    .select('role')
    .eq('id', session.user.id)
    .single() as { data: ProfileQueryResult, error: Error | null };

  if (profileError || profile?.role !== 'admin') {
    // If user is not admin, sign them out and redirect with error
    await supabase.auth.signOut();
    return NextResponse.redirect(
      `${requestUrl.origin}/admin/login?error=Unauthorized access. Not an admin.`,
      { status: 301 }
    );
  }

  // On successful login and admin role verification, redirect to admin dashboard
  return NextResponse.redirect(`${requestUrl.origin}/admin/dashboard`, { status: 301 });
}

import { createServerClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { redirect } from 'next/navigation';
import Link from 'next/link';

import type { Database } from '@/types/supabase';

interface ResponseCookieOptions {
  domain?: string;
  expires?: Date;
  httpOnly?: boolean;
  maxAge?: number;
  path?: string;
  sameSite?: boolean | 'lax' | 'strict' | 'none';
  secure?: boolean;
  value?: string;
}

export default async function AdminDashboardPage() {
  const cookieStore = await cookies();
  const supabase = createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return cookieStore.get(name)?.value;
        },
        set(name: string, value: string, options: ResponseCookieOptions) {
          cookieStore.set(name, value, options);
        },
        remove(name: string, options: { path: string }) {
          cookieStore.delete(name); // Removed options, simplified to delete by name
        },
      },
    }
  );

  const { data: { session } } = await supabase.auth.getSession();

  if (!session) {
    redirect('/admin/login');
  }

  type ProfileQueryResult = { role: string | null } | null;
  const { data: profile, error: profileError } = await supabase
    .from('profiles')
    .select('role')
    .eq('id', session.user.id)
    .single() as { data: ProfileQueryResult, error: Error | null };

  if (profileError || profile?.role !== 'admin') {
    await supabase.auth.signOut();
    redirect('/admin/login?error=Unauthorized access.');
  }

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100">
      <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-md">
